int32_t	big_int_print(__uint128_t *tab_nb, int16_t tab_size)
{
	int32_t i;
	int32_t j;
	__uint128_t carry;

	if (!tab_nb && ft_putstr_fd_v2("null", 2))
		return (0);

	i = 0;
	j = 0;
	carry = BIG_INT_CARRY;

	while (i < tab_size && !(tab_nb[i]))
		i++;
	if (i < tab_size && tab_nb[i])
		j += ft_putnbr_base(tab_nb[i++], 10, 0);
		// sleep(10000);
	while (i < tab_size)
	{
		if (tab_nb[i])
		{
			while (tab_nb[i] < carry / 10)
			{
				write(1, "0", 1);
				carry /= 10;
				j++;
			}
			carry = BIG_INT_CARRY;
			j += ft_putnbr_base(tab_nb[i], 10, 0);
		}
		else
		{
			write (1,"0000000000000000000000000000000000000", 37);
			j += 37;
		}
		i++;
	}
	return (j);
}


