__int128_t	mth_fast_pow(__int128_t nb , __int128_t exponent)
{
	if (exponent == 1)
		return (nb);
	if (!(exponent & 1))
		return (mth_fast_pow(nb * nb, exponent / 2));
	return (nb * mth_fast_pow(nb * nb, (exponent - 1) / 2));
}


