__uint128_t	*big_int_x(__uint128_t *tab_nb, uint16_t tab_size, uint16_t factor)
{
	uint16_t carry;

	if (!tab_nb || tab_size > BIG_INT_SIZE || factor > 34)
	{
		ft_putstr_fd_v2(RED"Big int x -> ∅\n"RESET, 2);
		return (tab_nb);
	}
	carry = 0;
	while (tab_size-- > 0)
	{
		tab_nb[tab_size] *= factor;
		tab_nb[tab_size] += carry;
		if ((carry = (tab_nb[tab_size] / BIG_INT_CARRY)))
			tab_nb[tab_size] %= BIG_INT_CARRY;
	}
	return (tab_nb);
}

uint64_t	big_int_count(__uint128_t *tab_nb, uint16_t tab_size)
{
	uint64_t figure;

	figure = 0;
	while (tab_size && !(*tab_nb))
	{
		tab_nb++;
		tab_size--;
	}
	while (tab_size && *tab_nb / 10)
	{
		*tab_nb /= 10;
		figure++;
	}
	if (tab_size && *tab_nb / 1)
		figure++;
	while (tab_size && --tab_size)
		figure += 37;
	return (figure);
}


