#define UINT128_MAX ((__uint128_t)0xFFFFFFFFFFFFFFFF * 0x1000000000000000 * 0x10 + 0xFFFFFFFFFFFFFFFF)
#define INT128_MIN ((__uint128_t)0x8000000000000000 * 0x1000000000000000 * 0x10)
#define INT128_MAX ((__uint128_t)0x8000000000000000 * 0x1000000000000000 * 0x10 - 1)
#define BIG_INT_MAX ((__uint128_t)9999999999999999999ULL * 1000000000000000000 + 999999999999999999)
#define BIG_INT_ONE_37 ((__uint128_t)1111111111111111111ULL * 1000000000000000000 + 111111111111111111)
#define BIG_INT_ONE_36 ((__uint128_t)1111111111111111111ULL * 100000000000000000 + 11111111111111111)
#define BIG_INT_NINE_36 ((__uint128_t)9999999999999999999ULL * 100000000000000000 + 99999999999999999)
#define BIG_INT_NINE_35 ((__uint128_t)9999999999999999999ULL * 10000000000000000 + 9999999999999999)

#define BIG_INT_CARRY ((__uint128_t)10000000000000000000ULL * 1000000000000000000)
#define BIG_INT_CARRY_37 ((__uint128_t)10000000000000000000ULL * 1000000000000000000 / 10)

