int	big_int_pow_2(int nb , int exponent)
{
	if (exponent == 1)
		return (nb);
	if (!(exponent & 1))
		return (big_int_pow_2(nb * nb, exponent / 2));
	return (nb * big_int_pow_2(nb * nb, (exponent - 1) / 2));
}


// ____uint128_t	big_int_(__uint128_t nb , uint32_t exponent)



__uint128_t	*big_int_x(__uint128_t *tab_nb, int16_t tab_size, int16_t multi)
{
	int16_t	 carry = 0;
	while (tab_size-- > 0)
	{
		tab_nb[tab_size] *= multi;
		tab_nb[tab_size] += carry;
		if ((carry = (tab_nb[tab_size] / BIG_INT_CARRY)))
			if ((tab_size - 1) > -1)
				tab_nb[tab_size] %= BIG_INT_CARRY;
	}
	return (tab_nb);
}

